mapper.update:
update orders
number=#{number}, status=#{status}, address_book_id=#{addressBookId}, order_time=#{orderTime}, checkout_time=#{checkoutTime}, pay_method=#{payMethod}, pay_status=#{payStatus}, amount=#{amount}, remark=#{remark}, phone=#{phone}, address=#{address}, user_name=#{userName}, consignee=#{consignee} , cancel_reason=#{cancelReason}, rejection_reason=#{rejectionReason}, cancel_time=#{cancelTime}, estimated_delivery_time=#{estimatedDeliveryTime}, delivery_status=#{deliveryStatus}, delivery_Time=#{deliveryTime}, pack_amount=#{packAmount}, tableware_number=#{tablewareNumber}, tableware_status=#{tablewareStatus},
where id=#{id}

mapper.select:
select * from orders


        and number like concat('%',#{number},'%')


        and phone like concat('%',#{phone},'%')


        and user_id = #{userId}


        and status = #{status}


        and order_time >= #{beginTime}


        and order_time <= #{endTime}


order by order_time desc

update.foreach:
update orders
number=#{order.number}, status=#{order.status}, address_book_id=#{order.addressBookId}, order_time=#{order.orderTime}, checkout_time=#{order.checkoutTime}, pay_method=#{order.payMethod}, pay_status=#{order.payStatus}, amount=#{order.amount}, remark=#{order.remark}, phone=#{order.phone}, address=#{order.address}, user_name=#{order.userName}, consignee=#{order.consignee}, cancel_reason=#{order.cancelReason}, rejection_reason=#{order.rejectionReason}, cancel_time=#{order.cancelTime}, estimated_delivery_time=#{order.estimatedDeliveryTime}, delivery_status=#{order.deliveryStatus}, delivery_time=#{order.deliveryTime}, pack_amount=#{order.packAmount}, tableware_number=#{order.tablewareNumber}, tableware_status=#{order.tablewareStatus},
where id = #{order.id}

mapper.select:
select sum(amount) from orders


        and order_time >= #{begin}


        and order_time <= #{end}

select count(id) from orders

    and order_time > #{begin}
    and order_time < #{end}
    and status=#{status}
